<?php
namespace Bpost;

use Bpost\BpostApiClient\Bpost\Label;
use Bpost\BpostApiClient\Exception\BpostLogicException\BpostInvalidValueException;

class LabelTest extends \PHPUnit_Framework_TestCase
{
    /**
     * Test validation in the setters
     */
    public function testFaultyProperties()
    {
        $label = new Label();

        try {
            $label->setMimeType(str_repeat('a', 9));
            $this->fail('BpostInvalidValueException not launched');
        } catch (BpostInvalidValueException $e) {
            // Nothing, the exception is good
        } catch (\Exception $e) {
            $this->fail('BpostInvalidValueException not caught');
        }

        // Exceptions were caught,
        $this->assertTrue(true);
    }

    public function testCreateFromXML()
    {
        $label = Label::createFromXML(new \SimpleXMLElement($this->getCreateLabelForOrderXml()));
        $this->assertSame('323299901059912015292030', $label->getBarcode()); // Test retro-compatibility
        $this->assertSame('application/pdf', $label->getMimeType());
        $this->assertCount(2, $label->getBarcodes());
        $this->assertSame($this->getCreateLabelForOrderBytes(), base64_encode($label->getBytes()));
    }

    private function getCreateLabelForOrderXml()
    {
        return str_replace('{bytes}', $this->getCreateLabelForOrderBytes(), <<< XML
<?xml version="1.0" encoding="UTF-8" standalone="yes"?>
<label
  xmlns="http://schema.post.be/shm/deepintegration/v3/"
  xmlns:ns2="http://schema.post.be/shm/deepintegration/v3/common"
  xmlns:ns3="http://schema.post.be/shm/deepintegration/v3/national"
  xmlns:ns4="http://schema.post.be/shm/deepintegration/v3/international">
  <barcodeWithReference>
    <barcode>323299901059912015292030</barcode>
    <reference>test_barcode_with_reference</reference>
  </barcodeWithReference>
  <barcodeWithReference>
    <barcode>323299901059912015293050</barcode>
    <reference>test_barcode_with_reference</reference>
  </barcodeWithReference>
  <mimeType>application/pdf</mimeType>
  <bytes>{bytes}</bytes>
</label>

XML
        );
    }

    private function getCreateLabelForOrderBytes()
    {
        return <<< BYTES

BYTES;
    }
}
